(8, 1, '2014-06-21 00:00:15', '2014-06-21 00:00:15', '[solr posttypes="violation" facets="media|Media,violation_type,profession,violation_event" page_size="20" ]\r\n<div class="post" style="border-bottom: 1px solid black;">\r\n\r\n<h2 class="title"><a href="$PERMALINK$" rel="bookmark">$TITLE$</a></h2>\r\n<div class="entry archive_entry">$TEASER$</div>\r\n<div class="pdate"><abbr class="date time published">$DATE$</abbr></div>\r\n<div class="term_link_div"><a class="term_link_red" href="$PERMALINK$">Read more</a></div>\r\n</div>\r\n[/solr]', 'Violations', '', 'publish', 'open', 'open', '', 'violations', '', '', '2014-06-21 23:01:25', '2014-06-21 23:01:25', '', 0, 'http://sitc.dev/?page_id=8', 0, 'page', '', 0),
